package hotsuop.daggers.entities;

import net.minecraft.entity.EntityType;
import net.minecraft.entity.LivingEntity;
import net.minecraft.entity.damage.DamageSource;
import net.minecraft.entity.player.PlayerEntity;
import net.minecraft.entity.projectile.thrown.ThrownItemEntity;
import net.minecraft.item.Item;
import net.minecraft.util.hit.EntityHitResult;
import net.minecraft.util.hit.HitResult;
import net.minecraft.world.World;
import hotsuop.daggers.items.Woodendagger;

public class CustomThrowableEntity extends ThrownItemEntity {

    public CustomThrowableEntity(EntityType<? extends ThrownItemEntity> entityType, World world) {
        super(entityType, world);
    }

    public CustomThrowableEntity(World world, LivingEntity owner) {
        super(EntityType.SNOWBALL, owner, world); // Use an existing entity type for simplicity
    }

    @Override
    protected Item getDefaultItem() {
        return new Woodendagger(new Item.Settings()); // Replace with your actual item
    }

    @Override
    protected void onCollision(HitResult hitResult) {
        super.onCollision(hitResult);
        if (!this.world.isClient) {
            this.world.sendEntityStatus(this, (byte) 3);
            this.remove(RemovalReason.DISCARDED);
        }
    }

    @Override
    protected void onEntityHit(EntityHitResult entityHitResult) {
        super.onEntityHit(entityHitResult);
        if (entityHitResult.getEntity() instanceof LivingEntity) {
            entityHitResult.getEntity().damage(DamageSource.thrownProjectile(this, this.getOwner()), 8.0F); // Adjust damage as needed
        }
    }

    @Override
    public Packet<?> createSpawnPacket() {
        return super.createSpawnPacket();
    }
}
