package deadlydaggers.entity;

import net.minecraft.entity.EntityType;
import net.minecraft.entity.LivingEntity;
import net.minecraft.entity.damage.DamageSource;
import net.minecraft.entity.projectile.PersistentProjectileEntity;
import net.minecraft.item.ItemStack;
import net.minecraft.nbt.NbtCompound;
import net.minecraft.network.PacketByteBuf;
import net.minecraft.network.listener.ClientPlayPacketListener;
import net.minecraft.network.packet.s2c.play.EntitySpawnS2CPacket;
import net.minecraft.sound.SoundEvent;
import net.minecraft.sound.SoundEvents;
import net.minecraft.util.hit.EntityHitResult;
import net.minecraft.util.hit.HitResult;
import net.minecraft.util.math.Vec3d;
import net.minecraft.world.World;
import deadlydaggers.item.DaggerItem;

public class ThrownDaggerEntity extends PersistentProjectileEntity {
    private ItemStack daggerStack;

    public ThrownDaggerEntity(EntityType<? extends PersistentProjectileEntity> entityType, World world) {
        super(entityType, world);
        this.daggerStack = new ItemStack(deadlydaggers.DeadlyDaggers.WOODEN_DAGGER);
    }

    public ThrownDaggerEntity(World world, LivingEntity owner, ItemStack stack) {
        super(((DaggerItem) stack.getItem()).getType(), owner, world);
        this.daggerStack = stack;
    }

    @Override
    public Packet<ClientPlayPacketListener> createSpawnPacket() {
        return new EntitySpawnS2CPacket(this);
    }

    @Override
    protected void onCollision(HitResult hitResult) {
        super.onCollision(hitResult);
        if (!this.world.isClient) {
            this.world.sendEntityStatus(this, (byte) 3);
            this.remove(RemovalReason.DISCARDED);
        }
    }

    @Override
    protected void onEntityHit(EntityHitResult entityHitResult) {
        super.onEntityHit(entityHitResult);
        if (entityHitResult.getEntity() instanceof LivingEntity) {
            entityHitResult.getEntity().damage(DamageSource.thrownProjectile(this, this.getOwner()), 8.0F); // Adjust damage as needed
        }
    }

    @Override
    protected ItemStack asItemStack() {
        return this.daggerStack;
    }

    @Override
    public void writeCustomDataToNbt(NbtCompound nbt) {
        super.writeCustomDataToNbt(nbt);
        nbt.put("Dagger", this.daggerStack.writeNbt(new NbtCompound()));
    }

    @Override
    public void readCustomDataFromNbt(NbtCompound nbt) {
        super.readCustomDataFromNbt(nbt);
        this.daggerStack = ItemStack.fromNbt(nbt.getCompound("Dagger"));
    }
}
